INSERT INTO `{tablename}_data_0` VALUES(18, 1, 8, '<p class="titdd-Article" style="font: 14px/28px 微软雅黑, Arial, Helvetica, sans-serif; margin: 0px 0px 28px; padding: 12px 18px 10px; color: rgb(0, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; white-space: normal; font-size-adjust: none; font-stretch: normal; background-color: rgb(243, 243, 243); -webkit-text-stroke-width: 0px;">徐若瑄5日凌晨在脸书分享儿子和咘咘的玩乐影片，只见咘咘坐在学步车里，而小V宝站在身后、踩着小步伐认真推车向前行。</p><p style="font: 16px/28px 微软雅黑, Arial, Helvetica, sans-serif; margin: 0px 0px 28px; padding: 0px; color: rgb(0, 0, 0); text-transform: none; text-indent: 2em; letter-spacing: normal; word-spacing: 0px; white-space: normal; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">&nbsp;</p><p style="font: 16px/28px 微软雅黑, Arial, Helvetica, sans-serif; margin: 0px 0px 28px; padding: 0px; text-align: center; color: rgb(0, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; white-space: normal; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;"><img style="border: 0px currentColor; border-image: none; vertical-align: bottom; max-width: 640px;" alt="徐若瑄儿子男友力爆表 为咘咘推学步车超萌" src="http://img1.gtimg.com/ent/pics/hv1/214/147/2151/139906474.png"/></p><p style="font: 10pt/28px 宋体; margin: 0px 0px 28px; padding: 0px; text-align: center; color: rgb(0, 0, 0); text-transform: none; text-indent: 0px; letter-spacing: normal; word-spacing: 0px; white-space: normal; font-size-adjust: none; font-stretch: normal; background-color: rgb(255, 255, 255); -webkit-text-stroke-width: 0px;">小V宝为咘咘推车</p><p style="font: 16px/28px 微软雅黑, Arial, Helvetica, sans-serif; margin: 0px 0px 28px; padding: 0px; color: rgb(0, 0, 0); text-transform: none; text-indent: 2em; letter-spacing: normal; word-spacing: 0px; white-space: normal; font-size-adjust: none; font-stretch: norm